import { useFlip, FlipProvider } from "react-easy-flip";
import {useState, useEffect} from "react";
import axios from "axios";

const map = "drivers/";
const filetype =".png";

const shuffle = function shuffle(array: any[]) {
  for (let i = array.length - 1; i > 0; i--) {
    let j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
    console.log(i)
  }
  return array;
};

  export interface Racer {
    id: number,
    code: string,
    firstname: string,
    lastname: string,
    country: string,
    team: string,
    [key: string]: any;
}



function ResultApp() {
  const [drivers, setDrivers] = useState<Racer[]>([]);

  const CombinedArray = drivers.map((item, index) => ({ 
    place: index + 1, 
    id: item.id,
    code: item.code, 
    firstname: item.firstname, 
    lastname: item.lastname, 
    team: item.team ,
    country: item.country,
    img: map + item.code + filetype,
    }));

    const result = CombinedArray.sort((a, b) => (a.place > b.place) ? 1 : -1);

    const hello = 1;

    const currentIndex = drivers.map((item, index) => (index));

    console.log(result, currentIndex)


  useEffect(() =>{
    fetch("/drivers.json")
    .then(resp => resp.json())
    .then((data: Racer[] )=> setDrivers(data))
    }, []);


  const todoItemsId = "flip-todo-items";

  useFlip(todoItemsId, {
    duration: 800,
  });

  const postList=()=> {

    axios.post('http://localhost:3000/drivers', {
    title: 'Steak',
    body: 'Is amazing',
    userId: 1,
    })
    .then(function (response) {
    console.log(response);
    })
    .catch(function (error) {
    console.log(error);
    }); 
    
}

  return (
    <FlipProvider>
      <div className="flex w-full h-full justify-center items-center bg-gray-800">
        <div className="w-1/2 text-center">
          <button
            className="mt-2 p-2 bg-gray-200 font-bold rounded-lg hover:bg-gray-400"
            onClick={() =>  setDrivers(shuffle([...drivers]))}
            >
            Race Start
          </button>
          <ul data-flip-root-id={todoItemsId} className="p-4">
            {drivers.map((item, index) => (
                
              <div
                key={item.id}
                data-flip-id={`flip-id-${item.id}`}
                className="flex items-center justify-center font-bold text-lg bg-gray-400 mb-4 bg-yellow-500 rounded-lg"
                >
                    <div className="race-place">{hello + index}</div>

                    <img className="racer" src={map + item.code.toLowerCase() + filetype} alt="racer" />

                    <div className="info-racer">
                        {item.firstname}<br/>
                        {item.lastname}
                    </div>
                    <div className="info-racer">
                        {item.country}<br/>
                        {item.team}
                    </div>

                    <div className="info-racer">
                        <button className="button" onClick={postList}><span>Overtake</span></button>
                    </div>
                     
              </div>
              
            ))}
          </ul>
        </div>
      </div>
    </FlipProvider>
  );
}

export default ResultApp;
